package com.linsy.prt.resource;import com.linsy.prt.config.ConvertData;import com.linsy.prt.domain.T_JHS_CollData;import com.linsy.prt.service.ICollDataService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;import javax.ws.rs.*;import javax.ws.rs.core.MediaType;import javax.ws.rs.core.MultivaluedMap;import java.util.List;/** * Created by Administrator on 2017/6/14. */@Component@Path("/colldata")public class CollDataRsource {    @Autowired    ICollDataService collDataService;    @POST    @Path("/upsert")    @Consumes({MediaType.APPLICATION_JSON, MediaType.APPLICATION_XML, MediaType.APPLICATION_FORM_URLENCODED})    @Produces({ MediaType.APPLICATION_JSON, MediaType.APPLICATION_XML, MediaType.APPLICATION_FORM_URLENCODED })    public boolean UpsertSendGoods(MultivaluedMap<String, String> queryParams) {        T_JHS_CollData jc=new T_JHS_CollData();        jc.setJuId(queryParams.getFirst("JuId"));        jc.setStarttime(ConvertData.StrToDate(queryParams.getFirst("Starttime")));        jc.setEndtime(ConvertData.StrToDate(queryParams.getFirst("Endtime")));        jc.setTreasureID(queryParams.getFirst("TreasureID"));        jc.setTreasureLink(queryParams.getFirst("TreasureLink"));        jc.setTreasureName(queryParams.getFirst("TreasureName"));        jc.setSaleQty(Integer.parseInt(queryParams.getFirst("SaleQty")));        jc.setStockQty(Integer.parseInt(queryParams.getFirst("StockQty")));        jc.setSumStockQty(Integer.parseInt(queryParams.getFirst("SumStockQty")));        jc.setActPrice(Double.parseDouble(queryParams.getFirst("ActPrice")));        jc.setPrice(Double.parseDouble(queryParams.getFirst("Price")));        jc.setShopName(queryParams.getFirst("ShopName"));        jc.setAct_id(Integer.parseInt(queryParams.getFirst("Act_id")));        jc.setPicUrl(queryParams.getFirst("PicUrl"));        jc.setRemindNum(Integer.parseInt(queryParams.getFirst("RemindNum")));        jc.setCreateDate(ConvertData.StrToDate(queryParams.getFirst("CreateDate")));        jc.setUpdatetime(ConvertData.StrToDate(queryParams.getFirst("Updatetime")));        jc.setExpire(Boolean.parseBoolean(queryParams.getFirst("IsExpire")));        return  collDataService.UpsertCollData(jc);    }    @GET    @Path("/get")    @Produces({ MediaType.APPLICATION_JSON, MediaType.APPLICATION_XML })    public List<T_JHS_CollData> GetTreasureCategoryAll(){        return collDataService.GetCollDataAll();    }}