package com.linsy.prt.resource;import com.linsy.prt.config.ConvertData;import com.linsy.prt.domain.T_Kingdee_SendGoods;import com.linsy.prt.service.ISendGoodsService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;import javax.ws.rs.Consumes;import javax.ws.rs.POST;import javax.ws.rs.Path;import javax.ws.rs.Produces;import javax.ws.rs.core.MediaType;import javax.ws.rs.core.MultivaluedMap;/** * Created by Administrator on 2017/6/13. */@Component@Path("/sendgoods")public class SendGoodsRsource {    @Autowired    private ISendGoodsService sendGoodsService;    @POST    @Path("/post")    @Consumes({MediaType.APPLICATION_JSON, MediaType.APPLICATION_XML, MediaType.APPLICATION_FORM_URLENCODED})    @Produces({ MediaType.APPLICATION_JSON, MediaType.APPLICATION_XML, MediaType.APPLICATION_FORM_URLENCODED })    public boolean UpsertSendGoods(MultivaluedMap<String, String> queryParams) {        T_Kingdee_SendGoods sd=new T_Kingdee_SendGoods();        sd.setK3Id(Long.parseLong(queryParams.getFirst("K3Id")));        sd.setItemCode(queryParams.getFirst("ItemCode"));        sd.setItemName(queryParams.getFirst("ItemName"));        sd.setSkuCode(queryParams.getFirst("SkuCode"));        sd.setSkuName(queryParams.getFirst("SkuName"));        sd.setQty(Double.parseDouble(queryParams.getFirst("Qty")));        sd.setSystemNo(queryParams.getFirst("SystemNo"));        sd.setSendGoodsNo(queryParams.getFirst("SendGoodsNo"));        sd.setSendGoodsBatchNo(queryParams.getFirst("SendGoodsBatchNo"));        sd.setPurchaseNo(queryParams.getFirst("PurchaseNo"));        sd.setStorageNo(queryParams.getFirst("StorageNo"));        sd.setBuyerNick(queryParams.getFirst("BuyerNick"));        sd.setProvince(queryParams.getFirst("Province"));        sd.setCity(queryParams.getFirst("City"));        sd.setArea(queryParams.getFirst("Area"));        sd.setReceiverAddress(queryParams.getFirst("ReceiverAddress"));        sd.setSupplierName(queryParams.getFirst("SupplierName"));        sd.setSendGoodsTime(ConvertData.StrToDate(queryParams.getFirst("SendGoodsTime")));        sd.setSendHouse(queryParams.getFirst("SendHouse"));        sd.setMadeBatchNo(queryParams.getFirst("MadeBatchNo"));        return  sendGoodsService.UpsertSendGoods(sd);    }}