package com.linsy.prt.resource;import com.linsy.prt.config.ConvertData;import com.linsy.prt.domain.T_JHS_TreasureCategory;import com.linsy.prt.service.ITreasureCategoryService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;import javax.ws.rs.*;import javax.ws.rs.core.MediaType;import javax.ws.rs.core.MultivaluedMap;import java.util.List;/** * Created by Administrator on 2017/6/15. */@Component@Path("/treasurecategoryrsource")public class TreasureCategoryRsource {    @Autowired    private ITreasureCategoryService treasureCategoryService;    @POST    @Path("/upsert")    @Consumes({MediaType.APPLICATION_JSON, MediaType.APPLICATION_XML, MediaType.APPLICATION_FORM_URLENCODED})    @Produces({ MediaType.APPLICATION_JSON, MediaType.APPLICATION_XML, MediaType.APPLICATION_FORM_URLENCODED })    public boolean UpsertTreasureCategory(MultivaluedMap<String, String> queryParams) {        T_JHS_TreasureCategory tc=new T_JHS_TreasureCategory();        tc.setTreasureID(queryParams.getFirst("TreasureID"));        tc.setCategoryName(queryParams.getFirst("Category_Name"));        tc.setShopName(queryParams.getFirst("ShopName"));        tc.setStatus(Integer.parseInt(queryParams.getFirst("Status")));        tc.setUpdateDate(ConvertData.StrToDate(queryParams.getFirst("UpdateDate")));        tc.setStyleName(queryParams.getFirst("StyleName"));        return  treasureCategoryService.UpsertTreasureCategory(tc);    }    @GET    @Path("/{TreasureID}")    @Produces({ MediaType.APPLICATION_JSON, MediaType.APPLICATION_XML })    public T_JHS_TreasureCategory queryBySkuCode(@PathParam("TreasureID") String TreasureID){        return treasureCategoryService.GetTreasureCategory(TreasureID);    }    @GET    @Path("/get")    @Produces({ MediaType.APPLICATION_JSON, MediaType.APPLICATION_XML })    public List<T_JHS_TreasureCategory> GetTreasureCategoryAll(){        return treasureCategoryService.GetTreasureCategoryAll();    }}