package com.linsy.prt.service.impl;import com.linsy.prt.dao.CollDataDao;import com.linsy.prt.domain.T_JHS_CollData;import com.linsy.prt.service.ICollDataService;import org.springframework.stereotype.Service;import javax.annotation.Resource;import java.util.List;/** * Created by Administrator on 2017/6/14. */@Service("colldataservice")public class CollDataServiceImpl implements ICollDataService {    @Resource    CollDataDao collDataDao;    public boolean UpsertCollData(T_JHS_CollData collData) {        return collDataDao.UpsertCollData(collData)==1;    }    public List<T_JHS_CollData> GetCollDataAll() {        return collDataDao.GetCollDataAll();    }}