package com.linsy.prt.service.impl;import com.linsy.prt.dao.TreasureCategoryDao;import com.linsy.prt.domain.T_JHS_TreasureCategory;import com.linsy.prt.service.ITreasureCategoryService;import org.springframework.stereotype.Service;import javax.annotation.Resource;import java.util.List;/** * Created by Administrator on 2017/6/15. */@Service("treasurecategoryservice")public class TreasureCategoryServiceImpl implements ITreasureCategoryService {    @Resource    TreasureCategoryDao treasureCategoryDao;    public boolean UpsertTreasureCategory(T_JHS_TreasureCategory treasureCategory) {        return treasureCategoryDao.UpsertTreasureCategory(treasureCategory)==1;    }    public T_JHS_TreasureCategory GetTreasureCategory(String TreasureID) {        T_JHS_TreasureCategory tc=treasureCategoryDao.GetTreasureCategory(TreasureID);        if (null==tc)            tc=new T_JHS_TreasureCategory();        return tc;    }    public List<T_JHS_TreasureCategory> GetTreasureCategoryAll() {        return treasureCategoryDao.GetTreasureCategoryAll();    }}